VLC="ResourceLifecycleConfig.VersionLifecycleConfig"

awscli elasticbeanstalk describe-applications --output table --query "sort_by(Applications,&ApplicationName)[$(auto_filter_joined ApplicationName -- "$@")].{\"1.Name\":ApplicationName,\"2.Versions\":Versions || '' | length(@), \"3.VersionLimit\":$VLC.MaxCountRule.Enabled, \"4.MaxVersions\":$VLC.MaxCountRule.MaxCount, \"5.DeleteSource\":$VLC.MaxCountRule.DeleteSourceFromS3, \"6.VersionAgeLimit\":$VLC.MaxAgeRule.Enabled, \"7.LimitInDays\":$VLC.MaxAgeRule.MaxAgeInDays, \"8.DeleteSource\":$VLC.MaxAgeRule.DeleteSourceFromS3}"