awscli iam list-instance-profiles --output table --query "InstanceProfiles[$(auto_filter_joined InstanceProfileName InstanceProfileId Roles[0].RoleName Roles[0].RoleId -- "$@")].{\"1.Name\":InstanceProfileName,\"2.Id\":InstanceProfileId,\"3.RoleName\":Roles[0].RoleName,\"4.RoleId\":Roles[0].RoleId}"